
var glslify			= require( 'glslify' );

var Texture			= require( 'gl-texture2d' );
var Shader			= require( 'gl-shader' );
var Fluid 			= require( '../Fluid' );

var drawTriangle 	= require( 'a-big-triangle' );

window.onload = function(){

	var raf = requestAnimationFrame;

	var canvas = document.createElement( 'canvas' );
	canvas.width = canvas.height = 512;
	document.body.appendChild( canvas );

	var gl = canvas.getContext( 'webgl' ) || canvas.getContext( 'webgl-experimental' ) || canvas.getContext( 'experimental-webgl' );

	var shader = Shader( gl,
		glslify( '../glsl/fluid.vert' ),
		glslify( './texture.frag' )
	);

	//var babs = Texture( gl, baboon );

	var fluid1d = Fluid( gl, {

		width: 256,
		height: 128,
		mode: '1d',
		wrap: false

	});

	var fluid2d = Fluid( gl, {

		width: 512,
		height: 512,
		mode: '2d',
		wrap: true

	});

	var drop = document.createElement( 'img' );
	drop.style.backgroundColor = '#00ffff';

	// 256 x 64
	//drop.src = 'data:image/png;base64,';
	// type
	//drop.src = 'data:image/png;base64,';
	// brush
	drop.src = 'data:image/png;base64,';
	document.body.appendChild( drop );

	var dropTex = null;
	drop.onload = function(){
		dropTex = Texture( gl, drop );
	};

	var down = false;
	var pos  = { x:0,y:0 };

	canvas.addEventListener( 'mousedown', function(ev){
		down = true;
		setPosition( ev );
	});
	window.addEventListener( 'mouseup', function(){
		down = false;
	});
	canvas.addEventListener( 'mousemove', function( ev ){
		setPosition( ev );
	});
	var setPosition = function( ev ){
		pos.x = ev.offsetX;
		pos.y = ev.offsetY;
	};


	raf( function render(){

		gl.clearColor( 0.0, 0.0, 0.0, 1.0 );
		gl.clear( gl.COLOR_BUFFER_BIT | gl.DEPTH_BUFFER_BIT );

		if( down ){

			var scalePos = [ fluid2d._shape[0] / 256, fluid2d._shape[1] / 256 ]; // for testing diff sizes

			if( pos.x < 256 && pos.y < 256 ){
				fluid2d.droplet( pos.x * scalePos[0], pos.y * scalePos[1], 0.5, 0.05, dropTex );
			}
		}

		fluid1d.update();
		fluid2d.update();

		// draw previews..

		shader.bind();

		var h = gl.canvas.height;

		fluid2d.texture().bind();
		gl.viewport( 0, h-256, 256, 256 );
		drawTriangle(gl);

		//fluid1d.texture().bind();
		//gl.viewport( 256, 0, 256, 512 );
		//drawTriangle(gl);

		raf( render );
	});



};